import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from "react";

function UserLikedMovies() {
  const user = useSelector((store) => store.user.user);
  const movieIdArray = user.likedMovies;
  const params = useParams();
  const userId = params.id;
  //   console.log(user);
  const [userMovieinfoArray, setUserMovieinfoArray] = useState([]);

  const getLikedMoviesCall = async (movieIdArray) => {
    await Promise.allSettled(
      movieIdArray.map(async (id) => {
        const response = await fetch(
          `https://api.themoviedb.org/3/movie/${id}?api_key=4503e42f5762f850ada999d307bfc5f9&language=en-US`
        );
        const data = await response.json();
        // console.log(data);
        setUserMovieinfoArray([data, ...userMovieinfoArray]);
        // return await data;
        // console.log(userMovieinfoArray);
      })
    );
  };

  useEffect(() => {
    console.log(getLikedMoviesCall(movieIdArray));
  }, []);

  return (
    <div>
      userLikedMovies
      <h3>{user.username}s liked movies</h3>
      {/* {userMovieinfoArray &&
        userMovieinfoArray.map((movie) => <p key={movie}>{movie.title}</p>)} */}
    </div>
  );
}

export default UserLikedMovies;
