// limitations under the License.
import React, { useEffect, useState } from "react";

function FeaturedSeries() {
  const TV_ENDPOINT = process.env;
  const [series, setSeries] = useState([]);

  const getSeries = async () => {
    try {
      const response = await fetch(
        `https://api.themoviedb.org/3/trending/tv/day/page=2?api_key=4503e42f5762f850ada999d307bfc5f9`
      );
      const data = await response.json();
      console.log(data.page);
      console.log(data.results);
      setSeries(data.results);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getSeries();
  }, []);

  return (
    <div className="featured-movie-section">
      <h2>Featured Movies</h2>
      <div className="movies-container">
        {series &&
          series.map((show) => (
            <div className="movie-card" key={show.id}>
              <img
                src={`https://image.tmdb.org/t/p/w500/${show.poster_path}`}
                alt="movie poster"
              />
              <div className="movie-card-text-overlay">
                {/* <p>{movie.overview}</p> */}
                <h4>{show.name}</h4>

                <div className="movie-card-overlay-btns">
                  <div>More</div>
                  <div>Like</div>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}

export default FeaturedSeries;
